// src/components/Dashboard.js
import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import {
  useTable,
  useSortBy,
  usePagination,
  useFilters,
} from "@tanstack/react-table";
import axios from "../axiosConfig";

const Dashboard = () => {
  const [dishes, setDishes] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDishes = async () => {
      try {
        const response = await axios.get("/dishes"); // Adjust the endpoint as needed
        setDishes(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching dishes:", error);
        setLoading(false);
      }
    };

    fetchDishes();
  }, []);

  const data = useMemo(() => dishes, [dishes]);

  const columns = useMemo(
    () => [
      {
        Header: "Dish Name",
        accessor: "name",
        Cell: ({ row }) => (
          <span
            style={{ color: "blue", cursor: "pointer" }}
            onClick={() => navigate(`/dishes/${row.original.id}`)}
          >
            {row.original.name}
          </span>
        ),
      },
      {
        Header: "Preparation Time",
        accessor: "prepTime",
      },
      {
        Header: "Cooking Time",
        accessor: "cookTime",
      },
      {
        Header: "Diet",
        accessor: "diet",
      },
      {
        Header: "Flavor",
        accessor: "flavor",
      },
      {
        Header: "State",
        accessor: "state",
      },
    ],
    [navigate]
  );

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state: { pageIndex },
    prepareRow,
  } = useTable(
    {
      columns,
      data,
      initialState: { pageIndex: 0, pageSize: 10 },
    },
    useFilters,
    useSortBy,
    usePagination
  );

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  {column.render("Header")}
                  <span>
                    {column.isSorted
                      ? column.isSortedDesc
                        ? " ðŸ”½"
                        : " ðŸ”¼"
                      : ""}
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {page.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
      <div>
        <button onClick={() => previousPage()} disabled={!canPreviousPage}>
          Previous
        </button>
        <span>
          Page{" "}
          <strong>
            {pageIndex + 1} of {pageOptions.length}
          </strong>{" "}
        </span>
        <button onClick={() => nextPage()} disabled={!canNextPage}>
          Next
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
