import React, { useState, useEffect } from "react";
import axios from "../axiosConfig";
import { Link } from "react-router-dom";
import { FaEye } from "react-icons/fa";
import "../styles/DishTable.css";

const DishTable = () => {
  const [dishes, setDishes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  const [totalPages, setTotalPages] = useState(1);
  const [sortConfig, setSortConfig] = useState({
    key: "name",
    direction: "asc",
  });
  const [selectedDishes, setSelectedDishes] = useState([]);
  const itemsPerPage = 10;

  useEffect(() => {
    fetchDishes();
  }, [currentPage, searchTerm, sortConfig]);

  const fetchDishes = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get("/dishes", {
        params: {
          page: currentPage,
          limit: itemsPerPage,
          search: searchTerm,
          sort_by: sortConfig.key,
          order: sortConfig.direction,
        },
      });
      setDishes(response.data);
      setTotalPages(response.data.totalPages);
    } catch (err) {
      setError("Failed to fetch dishes");
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (key) => {
    setSortConfig((prevConfig) => ({
      key,
      direction:
        prevConfig.key === key && prevConfig.direction === "asc"
          ? "desc"
          : "asc",
    }));
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    setCurrentPage(1);
  };

  const handleNext = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handlePrevious = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  const handleCheckboxChange = (dishId) => {
    setSelectedDishes((prevSelected) =>
      prevSelected.includes(dishId)
        ? prevSelected.filter((id) => id !== dishId)
        : [...prevSelected, dishId]
    );
  };

  const handleSelectAllChange = (event) => {
    if (event.target.checked) {
      const allDishIds = dishes.results.map((dish) => dish.id);
      setSelectedDishes(allDishIds);
    } else {
      setSelectedDishes([]);
    }
  };

  const handleSubmit = async () => {
    try {
      const response = await axios.post("/your-api-endpoint", {
        selectedDishes,
      });
      // Handle the response as needed
    } catch (err) {
      console.error("Failed to submit selected dishes", err);
    }
  };

  return (
    <div className="table-container">
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : (
        <>
          <table className="table">
            <thead>
              <tr>
                <th>
                  <input
                    type="checkbox"
                    onChange={handleSelectAllChange}
                    checked={
                      selectedDishes.length === dishes.results.length &&
                      dishes.results.length > 0
                    }
                    indeterminate={
                      selectedDishes.length > 0 &&
                      selectedDishes.length < dishes.results.length
                    }
                  />
                </th>
                <th onClick={() => handleSort("name")}>Dish Name</th>
                <th onClick={() => handleSort("prep_time")}>
                  Preparation Time
                </th>
                <th onClick={() => handleSort("cook_time")}>Cooking Time</th>
                <th onClick={() => handleSort("ingredients")}>Ingredients</th>
                <th onClick={() => handleSort("diet")}>Diet</th>
                <th onClick={() => handleSort("flavor_profile")}>Flavor</th>
                <th onClick={() => handleSort("state")}>State</th>
                <th onClick={() => handleSort("region")}>Region</th>
                <th>Details</th>
              </tr>
            </thead>
            <tbody>
              {dishes.results.map((dish) => (
                <tr key={dish.id}>
                  <td>
                    <input
                      type="checkbox"
                      checked={selectedDishes.includes(dish.id)}
                      onChange={() => handleCheckboxChange(dish.id)}
                    />
                  </td>
                  <td>{dish.name}</td>
                  <td>{dish.prep_time}</td>
                  <td>{dish.cook_time}</td>
                  <td>{dish.ingredients}</td>
                  <td>{dish.diet}</td>
                  <td>{dish.flavor_profile}</td>
                  <td>{dish.state}</td>
                  <td>{dish.region}</td>
                  <td>
                    <Link to={`/dishes/${dish.id}`}>
                      <FaEye />
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="pagination-controls">
            <button onClick={handlePrevious} disabled={currentPage === 1}>
              Previous
            </button>
            <span className="pagination-info">
              Page {currentPage} of {totalPages}
            </span>
            <button onClick={handleNext} disabled={currentPage === totalPages}>
              Next
            </button>
          </div>
          <button onClick={handleSubmit} disabled={selectedDishes.length === 0}>
            Submit Selected Dishes
          </button>
        </>
      )}
    </div>
  );
};

export default DishTable;
